import React from 'react';
import {
  Button, Input, Card, Icon,
} from 'antd';
import tableController from './tableController';

const TableView = () => {
  const {
    data,
    addPerson,
    setPersonName,
    deletePerson,
    editPerson,
    confirmEdit,
  } = tableController();

  return (
    <>

      <Input placeholder="name" value={data.personName} onChange={(e) => setPersonName(e.target.value)} />
      <Button onClick={() => addPerson()}>add</Button>
      <Button onClick={() => confirmEdit()}>confirm edit</Button>
      <>
        {data.personsList && data.personsList.map((item, index) => {
          const jsonPerson = item.toJSON();
          return (
            <Card
              key={index}
              title={jsonPerson.name}
              style={{ width: 300, marginTop: 16 }}
              actions={[
                <Icon type="delete" key="delete" onClick={() => deletePerson(index)} />,
                <Icon type="edit" key="edit" onClick={() => editPerson(index)} />,
                <Icon type="ellipsis" key="ellipsis" />,
              ]}
            />
          );
        })}
      </>

    </>
  );
};

export default TableView;
